import React, { Component } from "react";
import classes from "./show.module.css";
import { connect } from "react-redux";
class Show extends Component {
  state = {
    id: {
      data1: 1,
      data2: 1,
      data3: 1,
      data4: 1,
    },
  };
  clickHandler = (key, data) => {
    let arr1 = this.state.id;

    arr1[key] = !arr1[key];
    console.log(arr1);
    let arr = data;
    arr.push(key);
    this.setState({ clicked: !this.state.clicked });
    console.log(this.state.clicked);
    return this.props.saveToStore(arr, key);
  };
  render() {
    console.log("redner");
    if (!this.props.action) {
      let key = this.props.data[0];
      let data = Object.values(this.props.data[1]);
      return (
        <ul
          id={key}
          className={this.state.id[key] ? classes.table : classes.pink}
          onClick={() => {
            this.clickHandler(key, data);
          }}
        >
          <li>
            <a
              className={classes.linked}
              href="https://www.pluralsight.com/courses/react-flux-building-applications"
            >
              {data[0]}
            </a>
          </li>
          <li>{data[3]}</li>
          <li>{data[2]}</li>
          <li>{data[1]}</li>
        </ul>
      );
    }

    return <div></div>;
  }
}
const mapStateToProps = (state) => {
  return {
    action: state.action,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (arr, key) => {
      return dispatch({ type: "show", arr: { arr }, key: key });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Show);
