import React, { Component } from "react";
import classes from "./show.module.css";
import { connect } from "react-redux";
class Show extends Component {
  state = {
    name: null,
    title: null,
    author: null,
    length: null,
    category: null,
  };
  clickHandler = (key, data) => {
    let arr = data;
    arr.push(key);
    return this.props.saveToStore(data, key);
    return this.props.saveToStore(arr);
  };
  render() {
    if (!this.props.action) {
      let key = this.props.data[0];
      let data = Object.values(this.props.data[1]);
      return (
        <ul
          className={classes.table}
          onClick={() => {
            this.clickHandler(key, data);
          }}
        >
          <li>{data[0]}</li>
          <li>{data[1]}</li>
          <li>{data[2]}</li>
          <li>{data[3]}</li>
        </ul>
      );
    }

    return <div></div>;
  }
}
const mapStateToProps = (state) => {
  return {
    action: state.action,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (data, key) => {
      dispatch({ type: "store", data: { data }, key: { key } });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Show);
