import React, { Component } from "react";
import classes from "./show.module.css";
import classes1 from "./show1.module.css";
import { connect } from "react-redux";
class Show extends Component {
  state = {
    clicked: false,
    color: {
      data1: 0,
      data2: 0,
      data3: 0,
      data4: 0,
    },
  };
  selectHandler = () => {
    console.log("selected");
  };
  clickHandler = (key, data) => {
    let c = this.state.color;
    if (c[key]) {
      c[key] = 0;
    } else {
      c[key] = 1;
    }

    let arr = data;
    arr.push(key);
    console.log(this.state.color);
    return this.props.saveToStore(arr, key);
  };
  render() {
    if (!this.props.action) {
      let key = this.props.data[0];
      let data = Object.values(this.props.data[1]);
      // console.log(this.props.data);
      return (
        <ul
          id={key}
          className={!this.state.color[key] ? classes.table : classes.table1}
          onClick={() => {
            this.clickHandler(key, data);
          }}
        >
          <li>
            <a
              className={classes.linked}
              href="https://www.pluralsight.com/courses/react-flux-building-applications"
            >
              {data[0]}
            </a>
          </li>
          <li>{data[3]}</li>
          <li>{data[2]}</li>
          <li>{data[1]}</li>
        </ul>
      );
    }

    return <div></div>;
  }
}
const mapStateToProps = (state) => {
  return {
    action: state.action,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (arr, key) => {
      return dispatch({ type: "store", arr: { arr }, key: key });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Show);
