import React, { Component } from "react";
import classes from "./body.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { Link, Route, Switch } from "react-router-dom";
import Add from "../../component/add/add";
import Table from "../../component/table/table";
import { connect } from "react-redux";
import Cources from "../../component/Cources/cources";
import Buttons from "../../component/Buttons/Buttons";
class Body extends Component {
  state = {
    data1: {
      title: "Learning java",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data2: {
      title: "Learning python",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data3: {
      title: "Learning Ruby",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data4: {
      title: "Learning react",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data5: {
      title: "Learning english",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    count: 5,
  };
  render() {
    if (this.state.count + 1 === this.props.count) {
      const d1 = this.props.data;
      const title = "data" + this.props.count;
      let d2 = this.state;
      d2[title] = d1;
      this.setState({ count: this.state.count + 1 });
    }

    const d1 = Object.values(this.state);
    const d2 = d1.filter((ele) => {
      return typeof ele === "object";
    });
    let data1 = 1;
    const table = d2.map((ele) => {
      const values = Object.values(ele);
      const title = "data" + data1;
      data1 += 1;
      return (
        <div>
          <Table
            key={values[0] + values[1]}
            title={title}
            id_data={values[0]}
            data={values}
            num={this.props.count}
          />
        </div>
      );
    });

    const data = (
      <div className={classes.main_div}>
        <Cources />
        <div>{table}</div>
      </div>
    );
    return (
      <div>
        <Switch>
          <Route path="/add" component={Add} />{" "}
          <Route
            path="/"
            render={() => (
              <div>
                <Buttons />
                <table className={classes.table_style}>{data}</table>
              </div>
            )}
          />
        </Switch>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    data: state.data,
    count: state.count,
  };
};
export default connect(mapStateToProps)(Body);
