const initialState = {
  count: 6,
  formdata: null,
  formkey: null,
  // action: false,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    var index = "data" + state.count;
    return {
      ...state,
      count: state.count + 1,
      [index]: action.value,
    };
  }
  if (action.type === "store") {
    return {
      ...state,
      formdata: action.formdata,
    };
  }

  return state;
}
export default reducer;
