const initialState = {
  data: {},
  count: 5,
};
function reducer(state = initialState, action) {
  // console.log("redcer", action.value);
  if (action.type === "submit") {
    return {
      count: state.count + 1,
      data: action.value,
    };
  }
  return state;
}
export default reducer;
