import React, { Component } from "react";
import Cources from "../../component/Cources/cources";
import classes from "./body.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { Link, Route, Switch } from "react-router-dom";
import Add from "../../component/add/add";
import Table from "../../component/table/table";
import { connect } from "react-redux";
class Body extends Component {
  state = {
    data1: {
      title: "Learning java",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data2: {
      title: "Learning java",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data3: {
      title: "Learning java",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data4: {
      title: "Learning java",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    data5: {
      title: "Learning java",
      author: "siddhant",
      length: "20",
      category: "java",
    },
    count: 5,
  };
  render() {
    // console.log(this.state.count, this.props.count, this.state);
    if (this.state.count + 1 == this.props.count) {
      const d1 = this.props.data;
      const title = "data" + this.props.count;
      let d2 = this.state;
      d2[title] = d1;
      console.log(d2);
      this.setState({ count: this.state.count + 1 });
    }
    let num = 0;
    const table = Object.values(this.state).map((ele) => {
      num = num + 1;
      const values = Object.values(ele);
      return <Table key={num} data={values} num={num} />;
    });
    const data = (
      <div className={classes.buttons}>
        <Link to="/add">
          <button className={classes.New}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            New
          </button>
        </Link>
        <Link to="/add">
          <button className={classes.Edit}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            Edit
          </button>
        </Link>
        <Link to="/add">
          <button className={classes.Delete}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            Delete
          </button>
        </Link>
        <div className={classes.main_div}>
          <div className={classes.table}>
            <div className={classes.heading}>Title</div>
            <div className={classes.heading}>Author</div>
            <div className={classes.heading}>Category</div>
            <div className={classes.heading}>Length</div>
          </div>
          <div>{table}</div>
        </div>
      </div>
    );
    return (
      <div>
        <Switch>
          <Route path="/add" component={Add} />{" "}
          <Route path="/" render={() => data} />
        </Switch>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    data: state.data,
    count: state.count,
  };
};
export default connect(mapStateToProps)(Body);
