import React, { Component } from "react";
import "./add.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
class Add extends Component {
  state = {
    title: null,
    author: null,
    category: null,
    length: null,
    clear: true,
  };
  titleHandler = (event) => {
    const val = event.target.value;
    this.setState({ title: val, clear: false });
  };
  authorHandler = (event) => {
    const val = event.target.value;
    this.setState({ author: val });
  };
  categoryHandler = (event) => {
    const val = event.target.value;
    this.setState({ category: val });
  };
  lengthHandler = (event) => {
    const val = event.target.value;
    this.setState({ length: val });
  };
  onSubmitHandler = () => {
    var data = this.state;
    return this.props.saveToStore(data);
  };
  render() {
    return (
      <div className="main">
        <div className="form" id="form" onSubmit={this.formSubmitHandler}>
          <h1>Add</h1>
          <label>Title</label>
          <input
            placeholder="Title of cource"
            onChange={this.titleHandler}
          ></input>

          <label>Author</label>
          <select onChange={this.authorHandler}>
            <option></option>
            <option>Cory House</option>
            <option>Scot Allen</option>
            <option>Den Wahlin</option>
          </select>

          <label>Category</label>
          <input
            placeholder="Category of the cource"
            onChange={this.categoryHandler}
          ></input>

          <label>Length</label>
          <input
            placeholder="Length of the cource in minute and hours"
            onChange={this.lengthHandler}
          ></input>

          <div className="buttons">
            <Link to="/Cources">
              <button className="b1" onClick={this.onSubmitHandler}>
                <FontAwesomeIcon className="icons" icon={faPaperPlane} />
                Submit
              </button>
            </Link>
            <button
              className="b2"
              disabled={this.state.clear}
              onClick={this.clearHandler}
            >
              Clear
            </button>
            <Link to="/Cources">
              <button className="b3">Cancle</button>
            </Link>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (data) => {
      return dispatch({ type: "submit", value: data });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Add));
