import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
const Form = (props) => {
    const [data, setData] = useState();
    function onSubmitHandler() {
        console.log(data);
        return props.onSubmitHandler(data);
    }
    return (
        <div>
            <form onSubmit={onSubmitHandler}>
                <input placeholder="name" id="input" onChange={(event) => { setData(event.target.value) }}></input>
                <button onClick={props.onSubmitHandler}>Submit</button>
            </form>
        </div>
    );
}
const mapStateToProps = (state) => {
    return {
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
        onSubmitHandler: (data) => dispatch({ type: "submit", value: data }),
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(Form);
