const initialState = {
  data1: {
    title: "Learning java",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data2: {
    title: "Learning python",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data3: {
    title: "Learning Ruby",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data4: {
    title: "Learning react",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data5: {
    title: "Learning english",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  count: 6,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    console.log(action.value);
    var index = "data" + state.count;
    return {
      ...state,
      count: state.count + 1,
      [index]: action.value,
    };
  }
  if (action.type === "store") {
    // console.log(state.newdata);
    if (state.newdata === undefined) {
      return { ...state, newdata: action.arr };
    } else {
      let temp = state;
      delete temp["newdata"];
      return { ...temp };
    }
  }
  if (action.type === "editform") {
    return { ...state, [action.key]: action.value };
  }
  if (action.type === "delete") {
    let temp = state;
    delete temp[action.key];
    return { ...temp };
  }

  return state;
}
export default reducer;
