const initialState = {
  data1: {
    title: "Building Application in react and flux",
    author: "Cory House",
    category: "javascript",
    length: "20:08",
  },
  data2: {
    title: "Clean code:writing code for humans",
    author: "Cory House",
    category: "software practice",
    length: "12:09",
  },
  data3: {
    title: "Architecting Applications for the Real World",
    author: "Cory House",
    category: "Software Architecture",
    length: "10:65",
  },
  data4: {
    title: "Becoming an Outlier: Reprogramming the Developer Mind",
    author: "Cory House",
    category: "career",
    length: "12:56",
  },
  count: 5,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    console.log(action.value);
    var index = "data" + state.count;
    return {
      ...state,
      count: state.count + 1,
      [index]: action.value,
    };
  }
  if (action.type === "show") {
    if (state.newdata === undefined) {
      return { ...state, newdata: action.arr };
    } else {
      let arr1 = Object.values(action.arr)[0];
      let arr2 = Object.values(state.newdata)[0];
      if (arr1[4] === arr2[4]) {
        let temp = state;
        console.log(temp);
        delete temp["newdata"];
        console.log(temp);
        return { ...temp };
      } else {
        return { ...state, newdata: action.arr };
      }
    }
  }
  if (action.type === "editform") {
    return { ...state, [action.key]: action.value };
  }
  if (action.type === "delete") {
    let temp = state;
    delete temp[action.key];
    return { ...temp };
  }

  return state;
}
export default reducer;
