const initialState = {
  data1: {
    title: "Learning java",
    author: "siddhant",
    category: "20",
    length: "java",
  },
  data2: {
    title: "Learning python",
    author: "siddhant",
    category: "20",
    length: "java",
  },
  data3: {
    title: "Learning Ruby",
    author: "siddhant",
    category: "20",
    length: "java",
  },
  data4: {
    title: "Learning react",
    author: "siddhant",
    category: "20",
    length: "java",
  },
  data5: {
    title: "Learning english",
    author: "siddhant",
    category: "20",
    length: "java",
  },
  count: 6,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    var index = "data" + state.count;
    return {
      ...state,
      count: state.count + 1,
      [index]: action.value,
    };
  }
  if (action.type === "store") {
    let ele = Object.values(action.arr);
    console.log(ele[0]);
    if (ele[0].length > 0) {
      return { ...state, newdata: action.arr };
    } else {
      let temp = state;
      delete temp[action.key];
      return temp;
    }
  }
  if (action.type === "editform") {
    return { ...state, [action.key]: action.value };
  }
  if (action.type === "delete") {
    let temp = state;
    delete temp[action.key];
    return { ...temp };
  }

  return state;
}
export default reducer;
