import React, { Component } from "react";
import ShowData from "../showdata";
import { connect } from "react-redux"
class div extends Component {
    state = {
        name: "",
        tot: [],
        clicked: false,
    }
    onChangeHandler = (event) => {
        // console.log("onChangeHandler")
        const data = event.target.value;
        this.setState({ name: data });
        // console.log(this.state.name);
        // console.log("onChange Stops")
    }
    onClickHandler = () => {
        const data = this.state.tot;
        data.push(this.state.name);
        this.setState({ clicked: true, tot: data });
        return this.props.reduceHandler(this.state.tot);

    }
    render() {
        console.log("render")
        let showdata = null;
        if (this.props.clicked) {
            showdata = this.state.tot.map((ele) => {
                return <ShowData name={ele} />
            })
        }
        return (
            <div>
                <div>
                    <input placeholder="name" onChange={this.onChangeHandler}></input>
                    <button onClick={this.onClickHandler}>Submit</button>
                </div>
                {showdata}
            </div >
        );
    }
}
const mapStateToProps = (state) => {
    return {
        data: state.data,
        showData: state.showData,
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
        reduceHandler: (data) => { dispatch({ type: "submit", value: data }) }
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(div);
