import React, { Component } from "react";
class Form extends Component {
    state = {
        name: "",
    }
    onChangeHandler(event) {
        this.setState({ name: event.target.value });
    }
    render() {
        return (
            <div>
                <form>
                    <input placeholder="name" onChange={onChangeHandler}></input>
                    <button>Submit</button>
                </form>
            </div >
        );
    }
}
export default Form;
