const initialState = {
  data1: {
    title: "Building Application in react and flux",
    author: "Cory House",
    category: "javascript",
    length: "20:08",
  },
  data2: {
    title: "Clean code:writing code for humans",
    author: "Cory House",
    category: "software practice",
    length: "12:09",
  },
  data3: {
    title: "Architecting Applications for the Real World",
    author: "Cory House",
    category: "Software Architecture",
    length: "10:65",
  },
  data4: {
    title: "Becoming an Outlier: Reprogramming the Developer Mind",
    author: "Cory House",
    category: "career",
    length: "12:56",
  },
  color: { data1: null, data2: null, data3: null, data4: null },
  buttons: false,
  count: 5,
};
function reducer(state = initialState, action) {
  let colors = state.color;
  console.log(action, colors);
  if (action.type === "submit") {
    return {
      ...state,
      count: state.count + 1,
      ["data" + state.count]: action.value,
      buttons: false,
    };
  }
  if (action.type === "show") {
    // console.log(action.key, state, colors);
    let a = action.key;
    let arr = Object.entries(colors);
    let present = false;
    let idx1 = 0;
    let idx = -1;
    arr.map((ele) => {
      idx1++;
      if (ele[0] == a) {
        present = true;
        idx = idx1 - 1;
      }
    });
    console.log(arr, present, idx);
    if (present) {
      arr[idx][1] = !arr[idx][1];
    } else {
      arr.push([a, true]);
    }
    console.log(arr);
    if (state.newdata === undefined) {
      return { ...state, newdata: action.arr, buttons: true, color: arr };
    } else {
      let arr1 = Object.values(action.arr)[0];
      let arr2 = Object.values(state.newdata)[0];
      if (arr1[arr1.length - 1] === arr2[arr2.length - 1]) {
        let temp = state;
        delete temp["newdata"];
        return { ...temp, buttons: false, color: arr };
      } else {
        return { ...state, newdata: action.arr, buttons: true, color: arr };
      }
    }
  }
  if (action.type === "editform") {
    return { ...state, [action.key]: action.value, buttons: false };
  }
  if (action.type === "delete") {
    let temp = state;
    delete temp[action.key];
    return { ...temp, buttons: false };
  }
  if (action.type === "cancle") {
    return { ...state, buttons: false };
  }
  return state;
}
export default reducer;
