import React, { useState } from "react";
import { connect } from "react-redux";
const form = (props) => {
    const [name, setName] = useState();
    const [title, setTitle] = useState();
    return (
        <div>
            <form onSubmit={(event) => event.preventDefault()}>
                <input placeholder="name" onChange={(event) => setName(event.target.value)}></input>
                <input placeholder="title" onChange={(event) => setTitle(event.target.value)}></input>
                <button>Submit</button>
            </form>
        </div>
    );
}
const mapStateToProps = (state) => {
    return {
        name: state.name,
        title: state.title,
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
        onSubmitHandler: () => dispatch({ type: "submit", fname: name, ftitle: title })
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(form);
