import React, { Component } from "react";
import classes from "./Button.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
class Buttons extends Component {
  State = {
    redirect: false,
  };
  editHandler = () => {
    console.log(this.props.newdata);
    return <Link to="/dashboard"></Link>;
    // return <Redirect to="newdashboard" />;
  };
  render() {
    return (
      <div className={classes.cource}>
        <span className={classes.title}>Cources</span>
        <div className={classes.buttons}>
          <Link to="/add">
            <button className={classes.New}>
              <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
              New
            </button>
          </Link>

          <button className={classes.Edit} onClick={this.editHandler}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            Edit
          </button>
          <button className={classes.Delete} onClick={this.DeleteHandler}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            Delete
          </button>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    newdata: state.newdata,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {};
};
export default connect(mapStateToProps, mapDispatchToProps)(Buttons);
