import React, { Component } from "react";
import classes from "./show.module.css";
import classes1 from "./show.module.css";
import { connect } from "react-redux";
class Show extends Component {
  state = {
    name: null,
    title: null,
    author: null,
    length: null,
    category: null,
    clicked: false,
  };
  clickHandler = (key, data) => {
    let arr = data;
    arr.push(key);
    // console.log(arr);
    console.log(this.state.clicked);
    this.setState({ clicked: !this.state.clicked });
    return this.props.saveToStore(arr);
  };
  render() {
    if (!this.props.action) {
      let key = this.props.data[0];
      let data = Object.values(this.props.data[1]);
      return (
        <ul
          className={this.state.clicked ? classes.table : classes1.pink}
          onClick={() => {
            this.clickHandler(key, data);
          }}
        >
          <li>{data[0]}</li>
          <li>{data[1]}</li>
          <li>{data[2]}</li>
          <li>{data[3]}</li>
        </ul>
      );
    }

    return <div></div>;
  }
}
const mapStateToProps = (state) => {
  return {
    action: state.action,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (arr) => {
      return dispatch({ type: "store", arr: { arr } });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Show);
