import React, { Component } from "react";
import classes from "./show.module.css";
import { connect } from "react-redux";
class Show extends Component {
  state = {
    name: null,
    title: null,
    author: null,
    length: null,
    category: null,
  };
  clickHandler = (key, data) => {
    // console.log(key, data);
    this.setState({
      name: key,
      title: data[0],
      author: data[1],
      length: data[2],
      category: data[3],
    });
    return this.props.saveToStore(this.state, data, key);
  };
  render() {
    let key = this.props.data[0];
    let data = Object.values(this.props.data[1]);
    return (
      <ul
        className={classes.table}
        onClick={() => {
          this.clickHandler(key, data);
        }}
      >
        <li>{data[0]}</li>
        <li>{data[1]}</li>
        <li>{data[2]}</li>
        <li>{data[3]}</li>
      </ul>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (data, key) => {
      dispatch({ type: "store", data: { data }, key: { key } });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Show);
