import React, { Component } from "react";
import "./add.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
class Add extends Component {
  state = {
    title: null,
    author: null,
    category: null,
    length: null,
    submitted: false,
  };
  titleHandler = (event) => {
    const val = event.target.value;
    this.setState({ title: val, clear: true });
  };
  authorHandler = (event) => {
    const val = event.target.value;
    this.setState({ author: val, clear: true });
  };
  categoryHandler = (event) => {
    const val = event.target.value;
    this.setState({ category: val, clear: true });
  };
  lengthHandler = (event) => {
    const val = event.target.value;
    this.setState({ length: val, clear: true });
  };
  onSubmitHandler = () => {
    var data = this.state;
    console.log(data);
    if (data.title && data.author && data.length && data.category) {
      this.setState({ redirect: true });
      delete data["clear"];
      return this.props.saveToStore(data);
    }
  };
  formSubmitHandler = (event) => {
    event.preventDefault();
  };
  onClearHandler = () => {
    let ele = document.getElementById("form");
    ele.reset();
    this.setState({
      title: null,
      author: null,
      category: null,
      length: null,
      clear: false,
      textTitle: null,
      textAuthor: null,
      textCategory: null,
      textLength: null,
    });
  };
  render() {
    let redirect = null;
    if (this.state.redirect) {
      redirect = <Redirect to="/Cources" />;
    }
    return (
      <div className="main">
        {redirect}
        <form
          className="form"
          id="form"
          onSubmit={(event) => this.formSubmitHandler(event)}
        >
          <h1>Add</h1>
          <label>Title</label>
          <input
            placeholder="Title of cource"
            onChange={this.titleHandler}
            required
          ></input>
          <label>Author</label>
          <select onChange={this.authorHandler}>
            <option></option>
            <option>Cory House</option>
            <option>Scot Allen</option>
            <option>Den Wahlin</option>
          </select>

          <label>Category</label>
          <input
            placeholder="Category of the cource"
            onChange={this.categoryHandler}
          ></input>

          <label>Length</label>
          <input
            placeholder="Length of the cource in minute and hours"
            onChange={this.lengthHandler}
          ></input>

          <div className="buttons">
            <button className="b1" onClick={this.onSubmitHandler}>
              <FontAwesomeIcon className="icons" icon={faPaperPlane} />
              Submit
            </button>
            <button
              className="b2"
              disable={this.state.clear ? "false" : "true"}
              onClick={this.onClearHandler}
            >
              Clear
            </button>
            <Link to="/Cources">
              <button className="b3">Cancle</button>
            </Link>
          </div>
        </form>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};
const mapDispatchToProps = (dispatch) => {
  return {
    saveToStore: (data) => {
      return dispatch({ type: "submit", value: data });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Add));
