const initialState = {
  data1: {
    title: "Learning java",
    author: "siddhant",
    length: "20",
    category: "java",
  },
  data2: {
    title: "Learning python",
    author: "siddhant",
    length: "20",
    category: "java",
  },
  data3: {
    title: "Learning Ruby",
    author: "siddhant",
    length: "20",
    category: "java",
  },
  data4: {
    title: "Learning react",
    author: "siddhant",
    length: "20",
    category: "java",
  },
  data5: {
    title: "Learning english",
    author: "siddhant",
    length: "20",
    category: "java",
  },
  data6: {},
  count: 6,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    var index = "data" + state.count;
    return {
      ...state,
      [index]: action.value,
      count: state.count + 1,
    };
  }
  return state;
}
export default reducer;
