import React, { Component } from "react";
import classes from "./show.module.css";
class Show extends Component {
  state = {
    title: null,
    author: null,
    length: null,
    category: null,
  };
  clickHandler = () => {
    var data = this.props.data;
    // let element = document.getElementById(this.props.data[0]);
    // element.style.background("Black");
    this.setState({
      title: data[0],
      author: data[1],
      length: data[2],
      category: data[3],
    });
  };
  render() {
    let data = this.props.data;
    return (
      <ul className={classes.table} id={data[0]} onClick={this.clickHandler}>
        <li>{data[0]}</li>
        <li>{data[1]}</li>
        <li>{data[2]}</li>
        <li>{data[3]}</li>
      </ul>
    );
  }
}
export default Show;
