const initialState = {
  data: {},
  count: 5,
  toggle: false,
};
function reducer(state = initialState, action) {
  console.log("redcer", action.value);
  if (action.type === "submit") {
    return {
      ...state,
      count: state.count + 1,
      data: action.value,
      toggle: true,
    };
  }
  return state;
}
export default reducer;
