const initialState = {
  data: {},
  count: 0,
};
function reducer(state = initialState, action) {
  console.log("redcer", state.data);
  if (action.type === "submit") {
    return { ...state, count: state.count + 1, data: action.value };
  }
  return state;
}
export default reducer;
