import ShowData from "../showdata";
class div extends Component {
    state = {
        name: "",
    }
    onChangeHandler = (event) => {
        console.log("onChangeHandler")
        const data = event.target.value;
        this.setState({ name: data });
        // console.log(this.state.name);
        // console.log("onChange Stops")
    }
    showHandler = () => {
        console.log("shoeHandler");
        < ShowData name={this.state.name} />
    }
    render() {
        console.log("render")
        return (
            <div>
                <div>
                    <input placeholder="name" onChange={this.onChangeHandler}></input>
                    <button onClick={this.showHandler}>Submit</button>
                </div>
            </div >
        );
    }
}
export default div;
