import React, { Component } from "react";
import classes from "./table.module.css";
class Table extends Component {
  state = {
    flip: 0,
  };
  componentDidMount() {
    this.setState({ flip: this.state.flip + 1 });
  }
  render() {
    console.log(this.state.flip);
    const data = this.props.data;
    const style = this.props.num % 2 ? classes.even : classes.odd;
    return (
      <div className={[classes.table, style].join(" ")}>
        <span>{data[0]}</span>
        <span>{data[1]}</span>
        <span>{data[2]}</span>
        <span>{data[3]}</span>
      </div>
    );
  }
}

export default Table;
