const initialState = {
  data1: {
    title: "Building Application in react and flux",
    author: "Cory House",
    category: "javascript",
    length: "20:08",
  },
  data2: {
    title: "Clean code:writing code for humans",
    author: "Cory House",
    category: "software practice",
    length: "12:09",
  },
  data3: {
    title: "Architecting Applications for the Real World",
    author: "Cory House",
    category: "Software Architecture",
    length: "10:65",
  },
  data4: {
    title: "Becoming an Outlier: Reprogramming the Developer Mind",
    author: "Cory House",
    category: "career",
    length: "12:56",
  },
  editbutton: false,
  deletebutton: false,
  count: 5,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    console.log(action.value);
    var index = "data" + state.count;
    return {
      ...state,
      count: state.count + 1,
      [index]: action.value,
      editbutton: false,
      deletebutton: false,
    };
  }
  if (action.type === "store") {
    if (state.newdata === undefined) {
      return {
        ...state,
        newdata: action.arr,
        editbutton: false,
        deletebutton: false,
      };
    } else {
      let arr3 = Object.values(action.arr);
      let arr4 = Object.values(state.newdata);
      let arr1 = arr3[0];
      let arr2 = arr4[0];
      if (arr1[4] === arr2[4]) {
        let temp = state;
        delete temp["newdata"];
        return { ...temp, editbutton: false, deletebutton: false };
      } else {
        return {
          ...state,
          newdata: action.arr,
          editbutton: true,
          deletebutton: true,
        };
      }
    }
  }
  if (action.type === "editform") {
    return {
      ...state,
      [action.key]: action.value,
      buttons: { edit: false, delete: false },
    };
  }
  if (action.type === "delete") {
    let temp = state;
    delete temp[action.key];
    return { ...temp, deletebutton: false, editbutton: false };
  }

  return state;
}
export default reducer;
