import React, { Component } from "react";
import classes from "./Button.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { Route, Link, Redirect, Switch } from "react-router-dom";
import { connect } from "react-redux";
import Edit from "../edit/edit";
class Buttons extends Component {
  State = {
    redirect: false,
  };
  editHandler = () => {
    console.log(this.props.newdata);
  };
  render() {
    return (
      <Switch>
        <Route path="/edit" component={Edit} />
        <Route
          path="/Cources"
          render={() => {
            <div className={classes.cource}>
              <span className={classes.title}>Cources</span>
              <div className={classes.buttons}>
                <Link to="/add">
                  <button className={classes.New}>
                    <FontAwesomeIcon
                      icon={faPlus}
                      className={classes.fontAwsome}
                    />
                    New
                  </button>
                </Link>
                <Link
                  to={{
                    pathname: "/edit",
                    data: this.props.newdata,
                  }}
                >
                  <button className={classes.Edit} onClick={this.editHandler}>
                    <FontAwesomeIcon
                      icon={faPlus}
                      className={classes.fontAwsome}
                    />
                    Edit
                  </button>
                </Link>
                <button className={classes.Delete} onClick={this.DeleteHandler}>
                  <FontAwesomeIcon
                    icon={faPlus}
                    className={classes.fontAwsome}
                  />
                  Delete
                </button>
              </div>
            </div>;
          }}
        />
      </Switch>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    newdata: state.newdata,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {};
};
export default connect(mapStateToProps, mapDispatchToProps)(Buttons);
