import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
const Form = (props) => {
    return (
        <div>
            <form onSubmit={(event) => { event.preventDefault() }}>
                <input placeholder="name" onChange={props.onNameHandler}></input>
                <button onClick={props.onSubmitHandler}>Submit</button>
            </form>
        </div>
    );
}
const mapStateToProps = (state) => {
    return {
        name: state.name,
        title: state.title,
    };
}
const mapDispatchToProps = (dispatch) => {
    return {
        onNameHandler: (event) => dispatch({ type: "name", name: event.target.value }),
        onTitleHandler: (event) => dispatch({ type: "title", title: event.target.value }),
        onSubmitHandler: () => dispatch({ type: "submit" }),
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(Form);
