import React, { Component } from "react";
import Cources from "../../component/Cources/cources";
import classes from "./body.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { Link, Route, Switch } from "react-router-dom";
import Add from "../../component/add/add";
import Table from "../../component/table/table";
class Body extends Component {
  state = {
    data1: {
      title: "Learning java",
      author: "siddhant",
      length: 20,
      category: "java",
    },
    data2: {
      title: "Learning java",
      author: "siddhant",
      length: 20,
      category: "java",
    },
    data3: {
      title: "Learning java",
      author: "siddhant",
      length: 20,
      category: "java",
    },
  };
  render() {
    const table = Object.values(this.state).map((ele) => {
      const values = Object.values(ele);
      console.log(values);
      return <Table data={values} />;
    });
    const data = (
      <div className={classes.buttons}>
        <Link to="/add">
          <button className={classes.New}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            New
          </button>
        </Link>
        <Link to="/add">
          <button className={classes.Edit}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            Edit
          </button>
        </Link>
        <Link to="/add">
          <button className={classes.Delete}>
            <FontAwesomeIcon icon={faPlus} className={classes.fontAwsome} />
            Delete
          </button>
        </Link>
        <div className={classes.main_div}>
          <div className={classes.table}>
            <div className={classes.heading}>Title</div>
            <div className={classes.heading}>Author</div>
            <div className={classes.heading}>Category</div>
            <div className={classes.heading}>Length</div>
          </div>
          <div>{table}</div>
        </div>
      </div>
    );
    return (
      <div>
        <Switch>
          <Route path="/add" component={Add} />{" "}
          <Route path="/" render={() => data} />
        </Switch>
      </div>
    );
  }
}
export default Body;
