const initialState = {
  data1: {
    title: "Learning java",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data2: {
    title: "Learning python",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data3: {
    title: "Learning Ruby",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data4: {
    title: "Learning react",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  data5: {
    title: "Learning english",
    author: "siddhant",
    category: "java",
    length: "20",
  },
  count: 6,
};
function reducer(state = initialState, action) {
  if (action.type === "submit") {
    console.log(action.value);
    var index = "data" + state.count;
    return {
      ...state,
      count: state.count + 1,
      [index]: action.value,
    };
  }
  if (action.type === "store") {
    if (state.newdata === undefined) {
      return { ...state, newdata: action.arr };
    } else {
      console.log("action", action.arr);
      console.log("newdata", state.newdata);
      let arr3 = Object.values(action.arr);
      let arr4 = Object.values(state.newdata);
      console.log("match", arr3[0], arr4[0]);
      let arr1 = arr3[0];
      let arr2 = arr4[0];
      // if (arr1.some((item) => arr2.includes(item))) {
      //   console.log("same");
      // } else {
      //   console.log("different");
      // }
    }
  }
  if (action.type === "editform") {
    return { ...state, [action.key]: action.value };
  }
  if (action.type === "delete") {
    let temp = state;
    delete temp[action.key];
    return { ...temp };
  }

  return state;
}
export default reducer;
